import React from 'react';

class Home extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            bookData : null,
            bookDataLoaded : false,
            error : {status: false, message: 'No Error'}
        }
    }

    componentDidMount() {
        const headers = { 'Content-Type' : 'application/json' };

        fetch("http://localhost:3001/", {
            method: 'GET',
            mode: 'cors',
            headers
        })
        .then((result) => {
            if (result.status === 200) {
                result = result.json()
                    .then((result) => {
                        this.setState({
                            bookData: result,
                            bookDataLoaded: true
                        })
                    })
            }
            else if (result.status === 404) {
                this.setState({
                    bookDataLoaded: true,
                    error: {status: true, message: 'No Data Found'}
                });
            }
            else if (result.status === 400) {
                this.setState({
                    bookDataLoaded: true,
                    error: {status: true, message: 'Improper Data Used for GET Request'}
                });
            } else {
                this.setState({
                    bookDataLoaded: true,
                    error: {status: true, message: 'Unknown Error Occurred'}
                })
            }
        },
        (error) => {

        });
    }

    render() {
        return (
            <div>
                Home Placeholder Text
            </div>
        );
    }
}

export default Home;
